import React from 'react'
import { updateNewMessageBodyCreator, sendMessageCreator } from '../../redux/dialogs-reducer';
import Dialogs from './Dialogs';



const DialogsContainer = (props) => {

  debugger
  let state = props.state.getState();

  const onSendMessage = () => {
    props.state.dispatch(sendMessageCreator());
  }
  const onChangeNewMessage = (text) => {
    let action = updateNewMessageBodyCreator(text);
    props.state.dispatch(action);

  }
  return (
    <div>

      <Dialogs
        onSendMessage={onSendMessage}
        onChangeNewMessage={onChangeNewMessage}
        state={state.dialogsPage} 
        />
    </div>

  );

}
export default DialogsContainer ;
