import React from 'react';
import { reduxForm, Field } from 'redux-form';
import { Input } from '../common/FormsControls/FormsControls';
import { required } from '../../utils/validators/validator';
import { connect } from 'react-redux';
import {login} from '../../redux/auth-reducer'
import { Redirect } from 'react-router-dom';
const LoginForm = (props) => {
    return (

        <form onSubmit={props.handleSubmit}>
            <div>
                <Field validate={[required]} placeholder={"Login"} name={"email"} component={Input}/>
            </div>
            <div>
                <Field  validate={[required]} placeholder={"Password"} name={"password"} type={"password"} component={Input}/>
            </div>
            <div>
                <Field  validate={[required]} component={Input} type={"checkbox"} name={"rememberMe"} />
            </div>
            <div>
                <button>Login</button>
            </div>
        </form>
    )
}

const LoginReduxForm = reduxForm({form:'login'})(LoginForm)

const Login = (props) => {
    const onSubmit = (formData)=>{
        console.log(formData);
        props.login(formData.email, formData.password, formData.rememberMe);
    }
    if(props.Auth === true ){
      return  <Redirect to={"/profile"}/>
    }

    return <div>
        <h1>Login</h1>
        <LoginReduxForm onSubmit={onSubmit}/>
    </div>
}
export default connect(null, {login})(Login);
