const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET-USERS';

let initialState = {
    users: 
        [
            { id: 1, photoUrl: 'data:image/jpeg;base64,', followed: false, fullName: 'Dimitry', status: 'i am  a boss', location: { city: 'Minsk', country: 'Belarus' } },
            { id: 2, photoUrl: '', followed: true, fullName: 'Aman', status: 'i like football', location: { city: 'Moskva', country: 'Russia' } },
            { id: 3, photoUrl: '', followed: false, fullName: 'Esen', status: 'i am free', location: { city: 'Bishkek', country: 'Kyrgyzstan' } },
            { id: 4, photoUrl: '', followed: true, fullName: 'Bolot', status: 'i am  a teacher', location: { city: 'Kiev', country: 'Ukraine' } }

        ]




};
const UsersReducer = (state = initialState, action) => {
    switch (action.type) {
        case FOLLOW: {
           console.log("Esen"+ state.userId);
            let stateCopy = {
                ...state,
                users: state.users.map(u => {
                    if (u.id === state.userId) {
                        return { ...u, followed: true }
                    }
                    return u;
                })
            }
        }
        case UNFOLLOW: {
            let stateCopy = {
                ...state,
                users: state.users.map(u => {
                    if (u.id === state.userId) {
                        return { ...u, followed: false }
                    }
                    return u;
                })
            }
        }
        case SET_USERS: {
            return state;//{ ...state, users: [...state.users, ...action.users] }
        }
        default:
            return state;
    }
}

export const followAC = (userId) => ({ type: FOLLOW, userId })
export const unfollowAC = (userId) => ({ type: UNFOLLOW, userId })
export const setUsersAC = (users) => ({ type: SET_USERS, users })
export default UsersReducer;
