import React from 'react'
import s from './Dialogs.module.css';
import { NavLink, Redirect } from 'react-router-dom';
import Message from './Message/Message';
import DialogItem from './DialogItem/DialogItem';

const DialogForm = (props) => {
  return (
    <form>
      <div>
        <textarea
          placeholder="Enter your message"
          value={newMessageBody}
          onChange={onChangeNewMessage}>
        </textarea>
      </div >
      <div>
        <button onClick={onSendMessageClick}>send</button>
      </div>
    </form>
  )
}


const Dialogs = (props) => {



  let dialogElements = props.dialogsPages.dialogsData.map(dialog => <DialogItem name={dialog.name} id={dialog.id} />);
  let messagesElements = props.dialogsPages.messagesData.map(m => <Message message={m.message} />);
  let newMessageBody = props.dialogsPages.newMessageBody;

  let onSendMessageClick = () => {
    props.sendMessage();
  }
  let onChangeNewMessage = (e) => {
    let body = e.target.value;
    props.updateNewMessageBody(body);

  }


  return (
    <div className={s.dialogs}>
      <div className={s.dialogsItems}>
        {dialogElements}
      </div>

      <div className={s.messages}>
        {messagesElements}
        <div>
          <div>
            <DialogForm />
          </div>
        </div>


      </div>
    </div >
  );

}
export default Dialogs
